CREATE TABLE articles( article_id INTEGER NOT NULL PRIMARY KEY, temp data_type TEXT, title data_type TEXT UNIQUE, summary data_type TEXT, link data_type TEXT, published_date TEXT, source data_type TEXT, relevant data_type INT, table_constraints , sent_to_admin INTEGER, ml_prediction_gnb INTEGER, ml_prediction_lr INTEGER, sent_to_subscribers integer, discovery_date TEXT, sent_to_twitter integer, noun_phrases text);
CREATE TABLE categories( category_id INTEGER NOT NULL PRIMARY KEY, category_name TEXT );
CREATE TABLE rel_articles_categories( article_id INTEGER, category_id INTEGER, FOREIGN KEY (article_id) REFERENCES articles(article_id), FOREIGN KEY (category_id) REFERENCES categories(category_id) );
CREATE TABLE trials( trial_id INTEGER NOT NULL PRIMARY KEY, discovery_date data_type TEXT,  title TEXT type UNIQUE, summary data_type TEXT, link data_type TEXT, published_date TEXT, source data_type TEXT, relevant data_type INT, table_constraints, sent integer, sent_to_twitter integer);
